mapper.update:
update cart set cart_qty=#{cart_qty} 
where prod_code=#{prod_code} 
and user_id = #{user_id}

mapper.delete:
delete from cart 
where user_id = #{user_id} 
and prod_code = #{prod_code}

mapper.select:
select DISTINCT prod_color
from product
where prod_subcode=#{prod_subcode}

select DISTINCT prod_size
from product
where prod_subcode=#{prod_subcode}

mapper.update:
update cart set prod_code=
	(select prod_code from product 
	where prod_size=#{prod_size} 
	and prod_color=#{prod_color} 
	and prod_subcode=#{prod_subcode}),
	cart_qty = #{cart_qty}
where user_id = #{user_id}
and prod_code = #{prod_code}

update cart set cart_qty = #{cart_qty}
where user_id = #{user_id}
and prod_code = (select prod_code from product 
	where prod_size=#{prod_size} 
	and prod_color=#{prod_color} 
	and prod_subcode=#{prod_subcode})

mapper.select:
select cart_qty from cart
where prod_code = 
(select prod_code from product 
	where prod_size=#{prod_size} 
	and prod_color=#{prod_color} 
	and prod_subcode=#{prod_subcode})
and user_id = #{user_id}

mapper.delete:
delete from cart
where prod_code = (select prod_code from product 
	where prod_size=#{prod_size} 
	and prod_color=#{prod_color} 
	and prod_subcode=#{prod_subcode})
and cart_qty = #{cart_qty}
and user_id = #{user_id}
